
// stencilrouter: Custom Elements Define Library, ES Module/es2017 Target

import { defineCustomElement } from './stencilrouter.core.js';
import {
  AsyncContent,
  Redirect,
  Route,
  RouteLink,
  RouteSwitch,
  RouteTitle,
  Router,
  StencilRouterPrompt
} from './stencilrouter.components.js';

export function defineCustomElements(win, opts) {
  return defineCustomElement(win, [
    AsyncContent,
    Redirect,
    Route,
    RouteLink,
    RouteSwitch,
    RouteTitle,
    Router,
    StencilRouterPrompt
  ], opts);
}
